import React, {useState, useEffect} from 'react'
import { StyleSheet, View, Text, Image, FlatList,Button } from 'react-native'

import { connect } from 'react-redux'
import firebase from 'firebase'
require('firebase/firestore')

function Home(props) {
    const [posts, setPosts] = useState([]);


    useEffect(() => {
        let posts = [];
        if(props.usersLoaded == props.friend.length){
            for(let i = 0; i < props.friend.length; i++){
                const user = props.users.find(el => el.uid === props.friend[i]);
                if(user != undefined){
                    posts = [...posts, ...user.posts]
                }
            }
            posts.sort(function(x,y) {
                return x.creation - y.creation;
            })

            setPosts(posts);
        }

    }, [props.usersLoaded])

    if(user === null){
        return <View/>
    }
    return (
        <View style={styles.container}>
            <View style={styles.containerInfo}>
                <Text>{user.name}</Text>
                <Text>{user.email}</Text>

                    {props.route.params.uid !== firebase.auth().currentUser.uid ? (
                        <View>
                            {friend? (
                                <Button
                                    title="Friend"
                                    onPress={() => onUnfriend()}
                                />

                            ) : (
                                <Button
                                    title="Add Friend"
                                    onPress={() => onFriend()}
                                />
                            )}
                        
                        </View>
                    ) : null}
            </View>
            <View style={styles.containerGallery}>
                <FlatList
                    numColumns={3}
                    horizontal={false}
                    data={userPosts}
                    renderItem={({ item }) => (
                        <View
                            style={styles.containerImage}>

                            <Image
                                style={styles.image}
                                source={{ uri: item.downloadURL }}
                            />
                            <Text>Plant Name: {item.plantName}</Text>
                        </View>

                    )}
                
                />
                     

            </View>
        </View>
    )
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
    },
    containerInfo: {
        margin: 20
    },
    containerGallery: {
        flex: 1
    },
    containerImage: {
        flex: 1 / 3

    },
    image: {
        flex: 1,
        aspectRatio: 1 / 1
    }
})

const mapStateToProps = (store) => ({
    currentUser: store.userState.currentUser,
    
    friend: store.userState.friend,
    users: store.userState.users,
    usersLoaded: store.userState.usersLoaded,

})
export default connect(mapStateToProps, null)(Home);
